import { connect } from 'react-redux'
import logo from '../utils/WUR.jpeg'

class Login extends Component {

    render() { 
      const { users } = this.props
      return (
        <div class="container">        	
     		<div class="col-md-12">
        		<div class="row">
        			<h4><strong>Welcome To Would You Rather App!</strong></h4>
        			<div>Please sign in to continue</div>
        		</div>
        		<div class="row">
        			<img src={logo} class="img-thumbnail" alt="Would You Rather"/>
        		</div>
  				<div class="row" style={{marginTop:'20px',color:'darkGreen'}}>
        			<h4><strong>Sign In</strong></h4>
        		</div>
				<div class="row" style={{marginTop:'20px',color:'darkGreen'}}>
        			<div class="dropdown">
                      <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-							toggle="dropdown" aria-expanded="false">
                        Select A User
                      </button>
                      <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
						{replies.map((replyId) => (
                          <li key={replyId}>

                          </li>
                          ))}
                        <li><a class="dropdown-item" href="#">Action</a></li>
                        <li><a class="dropdown-item" href="#">Another action</a></li>
                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                      </ul>
					</div>
        		</div>
  			</div>     
        </div>    
      )
    }
  }

function mapStateToProps({users}) {

  let userArray = []

  Object.keys(users).forEach(function(key) {
    let obj = { "id": users[key].id, "name": users[key].name }; 
   	userArray.push(obj);
	});

   return {
     users: userArray
   }
}
  export default connect(mapStateToProps)(Login)
