import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import { Link, withRouter } from 'react-router-dom'
import { connect } from 'react-redux'

 const OPTION_ONE = 'optionOne'
 const OPTION_TWO = 'optionTwo'

class CardBody extends Component {

  state = {
    selectedOption : OPTION_ONE
  }

  onRBChange = (event, value) => {
      this.setState(() => ({
       selectedOption: value,
      })) 
  }


  render() { 
    const {question, isQuestionPage} = this.props
    console.log('state ', this.state)
    return (
            <div class="card-body" style={{textAlign:'left',borderLeft: '1px solid lightGray'}}>
             <h4 class="card-title h5 h4-sm">Would You Rather</h4>
				{ isQuestionPage ? (
                      <p class="card-text">
                          <div>
                          <label> <input name="r1" type="radio" checked={OPTION_ONE === this.state.selectedOption} 
                                  onChange={(e) => this.onRBChange(e,OPTION_ONE)} /></label>
						  <span> { question.optionOne.text } </span>
						  </div>
							<div>
                          <label> <input name="r1" type="radio" checked={OPTION_TWO === this.state.selectedOption}
                                  onChange={(e) => this.onRBChange(e, OPTION_TWO)} /></label>
							<span> { question.optionOne.text } </span>
						  </div>
                     </p>
					) :
					(
                  <p class="card-text">{question.optionOne.text} ...</p> 
                	) 

				}
            </div>
  		)
	}
}

function mapStateToProps(state,props) {
   return {
	props
   }
}
export default connect(mapStateToProps)(CardBody)
