import React, { Component } from 'react'
import { connect } from 'react-redux'
import { NavLink } from 'react-router-dom'
import { setAuthedUser } from '../actions/authedUser'

class TopNav extends Component {

 logOut = () => {
  const { dispatch } = this.props
  dispatch(setAuthedUser('')) 
 window.location.href="/"
 }

 render() { 
   const { username, avatar } = this.props
  return (   
      <ul className="navbar-nav ms-md-auto">
        <li className="nav-item" style={{display:'flex'}}>
        { username !== '' ?         			
       	<span className="nav-link nav-color"> Hello, {username}</span> : null}
		{ username !== '' ?         			
        <img src={avatar} alt={`Avatar of ${username}`} className='avatar img-thumbnail rounded-circle'/> : null}
        </li>
        <li className="nav-item">
        <a className="nav-link" onClick={this.logOut}> Log Out </a>       		
        </li>
      </ul>
  )
 }
}

function mapStateToProps({users,authedUser}) {
   return {
     username: authedUser && users ? users[authedUser].name : '',
     avatar: authedUser && users ? users[authedUser].avatarURL : '',

   }
}

export default connect(mapStateToProps)(TopNav)
