import '../App.css';
import React, { Component, Fragment } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import { connect } from 'react-redux'
import { handleInitialData } from '../actions/shared.js'
import Login from './Login'
import Home from './Home'
import Card from './Card'
import Nav from './Nav'
import LoadingBar from 'react-redux-loading'

class App extends Component {

  componentDidMount() {
    this.props.dispatch(handleInitialData())
  }

  render() {

    return (
      <Router>
        <Fragment>
          <LoadingBar />
          <div className='container col-md-12'>
      		<Nav/>
            {this.props.loading === true
              ? null
              : 
      <div className="row ">
      <div className="col-md-2 app_side"></div>
      <div className="col-md-8 App" style={{textAlign:'center'}}>
         <Route path='/' exact component={Login} />
 		<Route path='/home' exact component={Home} />
		<Route path='/questions/:id' component={Card} />
      </div>
      <div className="col-md-2 app_side nav-side-color"></div>
      </div>}
          </div>
        </Fragment>
      </Router>
    )
  }
}

//function mapStateToProps({authedUser}) {
  //return {
      //loading: authedUser === null ? true : false
    //}
//}
export default connect()(App)
