import React, { Component } from 'react'
import { connect } from 'react-redux'
import { NavLink } from 'react-router-dom'
import { setAuthedUser } from '../actions/authedUser'

class Nav extends Component {

 logOut = () => {
  const { dispatch } = this.props
  dispatch(setAuthedUser('')) 
// window.location.href="/"
 }

 render() { 
   const { username } = this.props
   console.log('username : ',username);
  return (
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
  		<div class="container-fluid">
    		<ul class="navbar-nav me-auto mb-2 mb-lg-0">
        		<li class="nav-item"> 
    				<NavLink to='/home' exact activeClassName='active' class="nav-link"> Home </NavLink>
       			</li>
    			<li class="nav-item"> 
    				<NavLink to='/leaderBoard' class="nav-link"> LeaderBoard </NavLink> 
    			</li>
     			<li class="nav-item">
        			<NavLink to='/newQuestion' class="nav-link"> New Question </NavLink>
    			</li>

    			<li class="nav-item">
    { username !== '' ?
        			<label class="nav-link"> Hello {username}</label> : null}
    			</li>

    			<li class="nav-item">
        			<NavLink class="nav-link" onClick={this.logOut}> Log Out </NavLink>
    			</li>
    		</ul>
    	</div>
    </nav>
  )
 }
}

function mapStateToProps({users,authedUser}) {
  console.log(users)
  console.log('a',authedUser)
   return {
     username: authedUser && users ? users[authedUser].name : ''
   }
}

export default connect(mapStateToProps)(Nav)
