import React, { Component } from 'react'
import { connect } from 'react-redux'
import { NavLink } from 'react-router-dom'
import { setAuthedUser } from '../actions/authedUser'

class Nav extends Component {

 logOut = () => {
  const { dispatch } = this.props
  dispatch(setAuthedUser('')) 
 window.location.href="/"
 }

 render() { 
   const { username, avatar } = this.props
  return (
    <nav class="navbar navbar-default" >
  <div class="container">



      <ul class="navbar-nav ">
        <li class="nav-item">
        <NavLink to='/home' exact activeClassName='active' class="nav-link"> Home </NavLink>
        </li>
        <li class="nav-item">
        <NavLink to='/leaderBoard' class="nav-link"> LeaderBoard </NavLink>
        </li>
        <li class="nav-item">
        <NavLink to='/newQuestion' class="nav-link"> New Question </NavLink>
        </li>
      </ul>
      <ul class="navbar-nav ms-md-auto">
        <li class="nav-item" style={{display:'flex'}}>
        { username !== '' ?         			
       	<span class="nav-link nav-color"> Hello, {username}</span> : null}
		{ username !== '' ?         			
        <img src={avatar} alt={`Avatar of ${username}`} className='avatar img-thumbnail rounded-circle'/> : null}
        </li>
        <li class="nav-item">
        <a class="nav-link" onClick={this.logOut}> Log Out </a>       		
        </li>
      </ul>
    </div>

</nav>
  )
 }
}

function mapStateToProps({users,authedUser}) {
   return {
     username: authedUser && users ? users[authedUser].name : '',
     avatar: authedUser && users ? users[authedUser].avatarURL : '',

   }
}

export default connect(mapStateToProps)(Nav)
