import { RECEIVE_QUESTIONS } from '../actions/questions.js'
export const SAVE_QUESTION_ANSWER = 'SAVE_QUESTION_ANSWER' ;

export default function questions ( state = [] , action) {
    switch (action.type) {
        case RECEIVE_QUESTIONS :
            return {
                ...state,
                ...action.questions
            }
        case SAVE_QUESTION_ANSWER :
        	return {
                ...state,
                ...action.questions
            }
            default :
            return state
    }
}
