import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import { Link, withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import CardBody from './CardBody'

class Card extends Component {
  render() { 
    const {user, id, isLeaderShip, isQuestionPage, question, hasAnswered, answer} = this.props

    return (
       	<div>
        	<div class="card card_color" style={{margin:'25px'}}>
              <div class="card-header">
				{isLeaderShip ?  <strong>{user.name} </strong> :
      			[<strong>{user.name} asks </strong>]
				}
              </div>
             <div class="card flex-row card_color">
				<img src={user.avatarURL} alt={`Avatar of ${user.name}`} 
                className='card-img-left avatar_card'/>
                <CardBody isQuestionPage ={isQuestionPage} 
                question={question} hasAnswered={hasAnswered} answer={answer}/>
              </div>
            </div>
        </div>    
  		)
	}
}

function mapStateToProps({users, questions, authedUser}, props) {
   const id = props.id ? props.id : props.match.params.id 
   const isLeaderShip = props.isLeaderShip
   const isQuestionPage = props.match ? (props.match.params.id ? true : false ) : false
   let user = {}
   if(isLeaderShip) {   
    user = users[id]
      return {
        user,
        isLeaderShip,
        isQuestionPage

       }
   }
   else {
   const question = questions[id]
   user = users[question.author]
   const hasAnswered = Object.keys(users[authedUser].answers).includes(id) ? true : false
      return {
      user,
      id,
      isLeaderShip,
      isQuestionPage,
      question,
      hasAnswered,
      answer: hasAnswered ? users[authedUser].answers[id]:''

     }
   }

}
export default connect(mapStateToProps)(Card)
