import React, { Component } from 'react'
import { connect } from 'react-redux'
import logo from '../utils/WUR.jpeg'
import { setAuthedUser } from '../actions/authedUser'

class Login extends Component {
  	state = {
        toHome: false,
    }

 	onUserChange = (event) => {
     console.log(event.target.value) 
     const { dispatch } = this.props
     dispatch(setAuthedUser(event.target.value))
      this.setState(() => ({
          toHome: true,
        })) 
    }


    render() { 
      const { users, authedUser } = this.props
      const { toHome } = this.state

	   if (toHome === true) {
            return <Redirect to='/' />
      }
      return (
        <div class="container">        	
     		<div class="col-md-12">
        		<div class="row">
        			<h4><strong>Welcome To Would You Rather App!</strong></h4>
        			<div>Please sign in to continue</div>
        		</div>
        		<div class="row">
        			<img src={logo} class="img-thumbnail" alt="Would You Rather"/>
        		</div>
  				<div class="row" style={{marginTop:'20px',color:'darkGreen'}}>
        			<h4><strong>Sign In</strong></h4>
        		</div>
				<select onChange={this.onUserChange} class="form-select" aria-label="Default select example">
                 <option value="" disabled selected>Please select a user</option>
                   { users.map(({id,name}) => (
                         <option value={id} key={id}> {name} </option>
                   ))} 
              </select>
  			</div>     
        </div>    
      )
    }
  }

function mapStateToProps({users,authedUser}) {

  let userArray = []

  Object.keys(users).forEach(function(key) {
    let obj = { "id": users[key].id, "name": users[key].name }; 
   	userArray.push(obj);
	});

   return {
     users: userArray,
     authedUser
   }
}
  export default connect(mapStateToProps)(Login)
