import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import { Link, withRouter } from 'react-router-dom'
import { connect } from 'react-redux'

class CardBody extends Component {
  state :{
    selectedOption : ''
  }  
  render() { 
    const {question, isQuestionPage} = this.props
    return (
            <div class="card-body" style={{textAlign:'left',borderLeft: '1px solid lightGray'}}>
             <h4 class="card-title h5 h4-sm">Would You Rather</h4>
				{ isQuestionPage ? (
                      <p class="card-text">
                 		<label>
            First Button: <input
              name="r1"
              type="radio"
              checked={true === this.state.myValue}
              onChange={e => {
                this.setState({ myValue: true});
              }}
            />
          </label>
          <label>
            Second button : <input
              name="r1"
              type="radio"
              checked={false === this.state.myValue}
              onChange={e => {
                this.setState({ myValue: false });
              }}
            />
          </label>
                     </p>
					) :
					(
                  <p class="card-text">{question.optionOne.text} ...</p> 
                	) 

				}
            </div>
  		)
	}
}

function mapStateToProps(state,props) {
   return {
	props
   }
}
export default connect(mapStateToProps)(CardBody)
