import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import { Link, withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import PollResults from './PollResults'
import Poll from './Poll'
import { handleSaveQuestionAnswer } from '../actions/questions'

 export const OPTION_ONE = 'optionOne'
 export const OPTION_TWO = 'optionTwo'

class PollCard extends Component {


  render() { 
    const {question, isQuestionPage, hasAnswered, answer} = this.props

    return (
            <div class="card-body" style={{textAlign:'left',borderLeft: '1px solid lightGray'}}>
				{ isQuestionPage ? 
                 [ hasAnswered ? 
                  (
                   <div class="card-text">
                      <div>
                 		<strong><h4>Results</h4></strong>
                 		<PollResults question={question} answer= {answer}/>
                 	  </div>
                   </div>
                   )
                 	 : 
                   	( 
                      <div>
                      <h4 class="card-title h5 h4-sm">Would You Rather</h4>                    
                         <Poll question={question}/>
                     </div>
					)
					] :
					(
                     <div> 
                    <h4 class="card-title h5 h4-sm">Would You Rather</h4>
                  	<p class="card-text">{question.optionOne.text} ...</p> 
					</div>
                	) 

				}
            </div>
  		)
	}
}

function mapStateToProps(state, props) {
   return {
    props
   }
}
export default connect(mapStateToProps)(PollCard)
