import React, { Component } from 'react'
import { connect } from 'react-redux'
import { NavLink } from 'react-router-dom'
import { setAuthedUser } from '../actions/authedUser'

class Nav extends Component {

 logOut = () => {
  const { dispatch } = this.props
  dispatch(setAuthedUser('')) 
 window.location.href="/"
 }

 render() { 
   const { username, avatar } = this.props
  return (
    <nav class="mb-1 navbar navbar-light navbar-expand-sm sticky-top nav-side-color" >
  <div class="container">

    <div class="navbar-nav-wrapper">

      <ul class="navbar-nav ml-auto">
        <li class="nav-item">
        <NavLink to='/home' exact activeClassName='active' class="nav-link"> Home </NavLink>
        </li>
        <li class="nav-item">
        <NavLink to='/leaderBoard' class="nav-link"> LeaderBoard </NavLink>
        </li>
        <li class="nav-item">
        <NavLink to='/newQuestion' class="nav-link"> New Question </NavLink>
        </li>
      </ul>

    </div>
  </div>
</nav>
  )
 }
}

function mapStateToProps({users,authedUser}) {
   return {
     username: authedUser && users ? users[authedUser].name : '',
     avatar: authedUser && users ? users[authedUser].avatarURL : '',

   }
}

export default connect(mapStateToProps)(Nav)
