import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import { Link, withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import CardBody from './CardBody'

class Card extends Component {
  render() { 
    const {user, id, question} = this.props
    return (
       	<div>
        	<div class="card" style={{margin:'25px'}}>
              <div class="card-header">
      			<strong>{user.name} asks </strong>
              </div>
             <div class="card flex-row ">
				<img src={user.avatarURL} alt={`Avatar of ${user.name}`} 
                className='card-img-left avatar_card'/>
                <CardBody question={question}/>
              </div>
            </div>
        </div>    
  		)
	}
}

function mapStateToProps({users,questions},props) {
	const id = props.id ? props.id : props.match.params.id   
   const question = questions[id]
   const user = users[question.author]
   return {
   	user,
	id: id,
    question
   }
}
export default connect(mapStateToProps)(Card)
