import { RECEIVE_USERS, SAVE_USER_ANSWER } from '../actions/users.js'

export default function users ( state = [] , action) {
    switch (action.type) {
        case RECEIVE_USERS :
            return {
                ...state,
                ...action.users
            }
      case SAVE_USER_ANSWER : 
        	return {
                ...state,
     			[action.authedUser]: {
       			...state[action.authedUser],
       			answers: {
         			...state[action.authedUser].answers,
         			[action.qid]: action.answer
       					}
     			}
            } 
        return {
                ...state,
                [action.id]: {
                    ...state[action.id],
                    [action.answer]: {
                        ...state[action.id][action.answer],
                        votes: state[action.id][action.answer].votes.concat([
                            action.authedUser,
                        ]),
                    },
                },
            };
            default :
            return state
    }
}
