    else if (hasAnswered) {
      return <strong>{user.name} asked </strong>
    } else {
      return <strong>{user.name} asks</strong>
    }
  }


  render() {
    const { question, isQuestionPage, hasAnswered, answer } = this.props

    return (
      <div
        class='card-body'
        style={{ textAlign: "left", borderLeft: "1px solid lightGray" }}>
        {isQuestionPage ? (
          [
            hasAnswered ? (
              <div className='card-text'>
                <div>
                  <strong>
                    <h4>Results</h4>
                  </strong>
                  <PollResults question={question} answer={answer} />
                </div>
              </div>
            ) : (
              <div>
                <h4 className='card-title h5 h4-sm'>Would You Rather</h4>
                <Poll question={question} />
              </div>
            ),
          ]
        ) : (
          <div>
            <h4 className='card-title h5 h4-sm'>Would You Rather</h4>
            <p className='card-text'>{question.optionOne.text} ...</p>
          </div>
        )}
      </div>
    )
  }
}

function mapStateToProps(state, props) {
  return {
    props,
  }
}
export default connect(mapStateToProps)(PollCard)
