import { RECEIVE_QUESTIONS, SAVE_QUESTION_ANSWER } from '../actions/questions.js'

export default function questions ( state = [] , action) {
    switch (action.type) {
        case RECEIVE_QUESTIONS :
            return {
                ...state,
                ...action.questions
            }
        case SAVE_QUESTION_ANSWER :        
        	 return {
                ...state,
                [action.id]: {
                    ...state[action.id],
                    [action.answer]: {
                        ...state[action.id][action.answer],
                        votes: state[action.id][action.answer].votes.concat([
                            action.authedUser,
                        ]),
                    },
                },
            };

            default :
            return state
    }
}
