import React, { Component } from "react"
import { connect } from "react-redux"
import PollCard from "./PollCard"
import LeaderCard from "./LeaderCard"

class Card extends Component {

  renderElement(){

      const {
      user,
      isLeaderShip,
      isQuestionPage,
    } = this.props


