      return  <strong>{user.name} asked </strong>
    }
    else {
      <strong>{user.name} asks</strong>
    }  
}

  render() {
    const {
      user,
      isLeaderShip,
      isQuestionPage,
      question,
      hasAnswered,
      answer,
    } = this.props

    return (
      <div>
        <div class='card card_color' style={{ margin: "25px" }}>
          <div class='card-header'>
           { this.renderElement() }
          </div>
          <div class='card flex-row card_color col-md-12'>
            <img
              src={user.avatarURL}
              alt={`Avatar of ${user.name}`}
              className='card-img-left avatar_card col-md-2'
            />
            <div class='col-md-8'>
              {isLeaderShip ? (
                <LeaderCard user={user} />
              ) : (
                <PollCard
                  isQuestionPage={isQuestionPage}
                  question={question}
                  hasAnswered={hasAnswered}
                  answer={answer}
                />
              )}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

function mapStateToProps({ users, questions, authedUser }, props) {
  const id = props.id ? props.id : props.match.params.id
  const isLeaderShip = props.isLeaderShip
  const isQuestionPage = props.match
    ? props.match.params.id
      ? true
      : false
    : false
  let user = {}
  if (isLeaderShip) {
    user = props.user
    return {
      user,
      isLeaderShip,
      isQuestionPage,
    }
  } else {
    const question = questions[id]
    user = users[question.author]
    const hasAnswered = Object.keys(users[authedUser].answers).includes(id)
      ? true
      : false
    return {
      user,
      id,
      isLeaderShip,
      isQuestionPage,
      question,
      hasAnswered,
      answer: hasAnswered ? users[authedUser].answers[id] : "",
    }
  }
}
export default connect(mapStateToProps)(Card)
