import { connect } from 'react-redux'

class CardBody extends Component {
  state :{
    selectedOption : ''
  }

  onUserChange = (event) => {
     const { dispatch } = this.props
     dispatch(setAuthedUser(event.target.value))
      this.setState(() => ({
          toHome: true,
        })) 
  }


  render() { 
    const {question, isQuestionPage} = this.props
    return (
            <div class="card-body" style={{textAlign:'left',borderLeft: '1px solid lightGray'}}>
             <h4 class="card-title h5 h4-sm">Would You Rather</h4>
				{ isQuestionPage ? (
                      <p class="card-text">
                 		<label> <input name="r1" type="radio" checked={optionOne === this.state.selectedOption} 
								onChange={e => {this.setState({ myValue: true});}} /></label>
          				<label> <input name="r1" type="radio" checked={optionTwo === this.state.selectedOption}
              					onChange={e => { this.setState({ myValue: false }); }} /></label>
                     </p>
					) :
					(
                  <p class="card-text">{question.optionOne.text} ...</p> 
                	) 

				}
            </div>
  		)
	}
}

function mapStateToProps(state,props) {
   return {
	props
   }
}
export default connect(mapStateToProps)(CardBody)
